import "core-js/modules/es.array.push.js";
import axios from "axios";
import AppNavbar from "./navbar.vue";
import AppFooter from "./footer.vue";
export default {
  name: "ProductList",
  data() {
    return {
      autoScrollInterval: null,
      items: [],
      showChat: false,
      showMessage: false,
      message: '',
      chatInterval: null,
      chatOpened: false,
      currentMessage: '',
      messages: [],
      showTypingEffect: false
    };
  },
  created() {
    this.getInstituciones();
  },
  methods: {
    startAutoScroll() {
      const scrollContainer = document.getElementById("scroll-container");
      if (scrollContainer !== null) {
        scrollContainer.scrollTop += 1; // Ajusta la velocidad de desplazamiento cambiando este valor
      } else {
        console.error("El elemento 'scroll-container' no se encontró en el DOM.");
      }
    },
    stopAutoScroll() {
      clearInterval(this.autoScrollInterval);
    },
    async sendMessage(message) {
      this.messages.push({
        from: 'user',
        data: message
      });
      await axios.post('http://localhost:3000/chatbot?message=' + message, {
        message: message
      }).then(response => {
        this.messages.push({
          from: 'chatGpt',
          data: response.data.data // Accede a la propiedad 'data' del objeto de respuesta
        });

        this.$nextTick(() => {
          const scrollContainer = document.getElementById("scroll-container");
          scrollContainer.scrollTop = scrollContainer.scrollHeight; // Desplaza automáticamente al final del contenedor de mensajes
        });
      });
    },

    toggleChat() {
      this.showChat = !this.showChat;
      this.chatOpened = this.showChat;
    },
    startPopupInterval() {
      this.chatInterval = setInterval(() => {
        this.showPopupMessage();
      }, 3000);
    },
    showPopupMessage() {
      if (!this.chatOpened) {
        this.message = '¿Tienes preguntas?';
        this.showMessage = true;
        setTimeout(() => {
          this.showMessage = false;
          this.message = '';
        }, 2000);
      }
    },
    async getInstituciones() {
      try {
        const response = await axios.get("http://localhost:3000/instituciones");
        this.items = response.data;
      } catch (err) {
        console.log(err);
      }
    },
    async deleteInstituciones(id) {
      try {
        await axios.delete(`http://localhost:3000/instituciones/${id}`);
        this.getInstituciones();
      } catch (err) {
        console.log(err);
      }
    },
    verPagina(id) {
      // Abrir otra página y enviar el ID del item como parámetro en la URL
      //window.open(`/PaginaInstitucion?id=${id}`, "_blank");
      window.open(`/PaginaInstitucion/${id}`);
    },
    /*Métodos Navbar*/
    irACalendarioCharlas() {
      this.$router.push("/calendario_charlas");
    },
    irAInstituciones() {
      this.$router.push("/instituciones");
    }
    /*Fin métodos navbar*/
  },

  beforeUnmount() {
    clearInterval(this.chatInterval);
  },
  /*SCRIPTS NAVBAR*/
  mounted() {
    document.addEventListener("DOMContentLoaded", () => {
      this.startAutoScroll();
    });
    this.startPopupInterval();
    const navbar = document.querySelector('.navbar');
    const menuBtn = document.querySelector('#menu-btn');
    menuBtn.addEventListener('click', () => {
      navbar.classList.toggle('active');
    });
    window.addEventListener('scroll', () => {
      navbar.classList.remove('active');
    });

    /*script botón que lleva al inicio de la página*/

    /*FIN DE BOTÓN*/
  },

  components: {
    AppNavbar,
    AppFooter
  }
};
